com.tainmao.service.impl.mapper.select:
/* SELECT * FROM admin*/ SELECT a.*, ar.name AS roleName FROM admin a LEFT JOIN admin_with_role awr ON awr.admin_id = a.id LEFT JOIN admin_role ar ON ar.id = awr.role_id
AND a.id = #{id}
AND a.username LIKE CONCAT(#{username},'%')
AND a.status = #{status}

com.tainmao.service.impl.mapper.update:
UPDATE admin
username = #{username}, password = #{password}, password_salt = #{passwordSalt}, nickname = #{nickname}, mobile = #{mobile}, email = #{email}
WHERE id = #{id}
